Ext.define('MyApp.controller.RootController', {
    extend: 'Ext.app.Controller',
    config: {
        routes: {
            'station/:swuid': 'departure',
            '': 'station'
        }
    },

    departure: function(swuid) {
        Ext.getCmp("departureTimeList").fireEvent("updateList", swuid);
        
        var card = Ext.getCmp("departureTimeList");
        Ext.getCmp("root").setActiveItem(card);
    },
    
    station: function(){
        console.info("load stationslist");
        var card = Ext.getCmp("stationList");
        Ext.getCmp("root").setActiveItem(card);
    }

});